import React from 'react';

class NavMobile extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			toggle: false,
			navSide: 'right',
			menu: props.menuItems,
			type: props.menuType
		}

		this.handleToggle = this.handleToggle.bind(this);
		this.switchSideRight = this.switchSideRight.bind(this);
		this.switchSideLeft = this.switchSideLeft.bind(this);
	}

	handleToggle() {
		(this.state.toggle === true) ? 
		this.setState({toggle: false}) :
		this.setState({toggle: true});
	}

	switchSideRight() {
		(this.state.navSide === 'left') ?
		this.setState({navSide: 'right'}) :
		null;
	}

	switchSideLeft() {
		(this.state.navSide === 'right') ?
		this.setState({navSide: 'left'}) :
		null;
	}

	render() {

		let menuState = (this.state.toggle === false) ? 'closed' : 'open';
		let menuSide = (this.state.navSide === 'right') ? 'nav-on-right' : 'nav-on-left';

		return (
			<div className={'mobile-nav-wrap ' + menuSide }>
				<button onClick={this.switchSideLeft} className='toggle-nav-placement toggle-left'>Move here</button>
				<button onClick={this.handleToggle} className='mobile-nav-button'>Menu</button>
				<button onClick={this.switchSideRight} className='toggle-nav-placement toggle-right'>Move here</button>
				<ul className={this.state.type + '-nav mobile-nav ' + menuState }>
					{this.state.menu}
				</ul>
			</div>
		);
	}
}

export default NavMobile;
