import React from 'react';

class NavMobile extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			toggle: false,
			menu: props.menuItems,
			type: props.menuType
		}

		this.handleToggle = this.handleToggle.bind(this);
	}

	handleToggle() {
		(this.state.toggle === true) ? 
		this.setState({toggle: false}) :
		this.setState({toggle: true});
	}

	render() {

		let menuState = (this.state.toggle === false) ? 'closed' : 'open';

		return (
			<div className='mobile-nav-wrap'>
				<button className='toggle-nav-placement toggle-right'>Move here</button>
				<button onClick={this.handleToggle} className='mobile-nav-button'>Menu</button>
				<button className='toggle-nav-placement toggle-left'>Move here</button>
				<ul className={this.state.type + '-nav mobile-nav ' + menuState }>
					{this.state.menu}
				</ul>
			</div>
		);
	}
}

export default NavMobile;
