import React from 'react';
import './FilterItems.css';

class Filter extends React.Component {

	render() {
		return(
			<nav className='filter'>
				Sort items:
				<ul>
					<li class='bounce' style='animation-delay:0.06s;'><button onClick={(e) =>
						this.props.filterUpdate(e, 'All')}>All</button></li>
					<li class='bounce' style='animation-delay:0.12s;'><button onClick={(e) =>
						this.props.filterUpdate(e, 'Web')}>Web</button></li>
					<li class='bounce' style='animation-delay:0.18s;'><button onClick={(e) =>
						this.props.filterUpdate(e, 'Branding')}>Branding</button></li>
					<li class='bounce' style='animation-delay:0.24s;'><button onClick={(e) =>
						this.props.filterUpdate(e, 'Graphic')}>Graphic</button></li>
				</ul>
			</nav>
		);
	}
}

export default Filter;
