import React from 'react';
import { Route } from 'react-router-dom';
import Filter from './FilterItems';
import PortfolioItem from './PortfolioItem';
import ProjectDisplay from './ProjectDisplay';
import { portfolioElements } from './Content';


class PortfolioAssemble extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			filter: null
		};

		this.filterUpdate = this.filterUpdate.bind(this);

	}

	filterUpdate(event, type) {
		this.setState({
			filter: {type}
		})
	}

	render() {
console.log(this.state);
		const shuffle = array => {
			for (let i = array.length - 1; i > 0; i--) {
				let j = Math.floor(Math.random() * (i + 1)); // Random index from 0 to i

				// Swap elements array[i] and array[j] (using destructing assignmnet)
				[array[i], array[j]] = [array[j], array[i]];
			}
			return array;
		}

		let shuffledPortfolio = shuffle(portfolioElements);

		let filteredPortfolio = this.state === null ? filteredPortfolio = shuffledPortfolio : shuffledPortfolio.filter(work => work.type.toLowerCase() == this.state.filter);

		const portfolioLinkAssembly = Object.keys(filteredPortfolio).map(key => 
	    	<PortfolioItem key={key} item={filteredPortfolio[key]} link={key} />
		)

		const portfolioItemAssembly = Object.keys(filteredPortfolio).map(element =>
			<Route key={element} path={filteredPortfolio[element].id} render={(props) => <ProjectDisplay {...props} position={element} />} />
		)

		return (
			<React.Fragment>
				<Filter
					filterUpdate={this.filterUpdate.bind(this)}
				/>
				{portfolioLinkAssembly}
				{portfolioItemAssembly}
			</React.Fragment>
		);
	}
}

export default PortfolioAssemble;
