import React from 'react';

class Filter extends React.Component {

	render() {
		return(
			<nav className='filter'>
				Sort items:
				<ul>
					<li><button onClick={this.props.filterUpdate(all)}>All</button></li>
					<li><button onClick={this.props.filterUpdate(web)}>Web</button></li>
					<li><button onClick={this.props.filterUpdate(branding)}>Branding</button></li>
					<li><button onClick={this.props.filterUpdate(graphic)}>Graphic</button></li>
				</ul>
			</nav>
		);
	}
}

export default Filter;
