function ThemeStyle(props) {

	let darkmode = props.mode;
	
	return darkmode;
};

export default ThemeStyle;

export const lightTheme = {
	body: '#FFF',
    text: '#363537',
    toggleBorder: '#FFF',
    background: '#363537',
}

export const darkTheme = {
    body: '#363537',
    text: '#FAFAFA',
    toggleBorder: '#6B8096',
    background: '#999',
}
