import React from 'react';
import { Link } from 'react-router-dom';


class NavMobile extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			toggle: false,
			content: props.list,
			type: props.type
		}

		this.handleToggle = this.handleToggle.bind(this);

	}

	handleToggle() {
		(this.state.toggle === true) ? 
		this.setState({toggle: false}) :
		this.setState({toggle: true});
	}

	navAssemble() { 
		content.map(element => {
			return (
				<li key={element.order}>
					<Link to={element.url}>
						{element.label}
					</Link>
				</li>
			);
		});
	}

	render() {
		return (
			<nav className='mobile-nav-wrap'>
				<button className='mobile-nav-button'></button>
				<ul className={type + '-nav mobile-nav'}>
					{navAssemble}
				</ul>
			</nav>
		);
	}
}

export default NavMobile;
