import React, { useState, useEffect } from 'react';

function ThemeStyle(props) {

	let darkmode = (props.mode) ? 'Darkmode' : 'Lightmode';


	useEffect(() => {
		document.body.classList.add('darkmode');
		}) ;

	
	return darkmode;
};

export default ThemeStyle;
