function ThemeStyle(props) {

	let theme = (props.mode === true) ? 'darkmode' : 'lightmode';

	return({
		backgroundColor: (theme === 'darkmode') ? 'darkgray' : 'lightgray',
	});
};

export default ThemeStyle;
